def new_centroids(list_of_clusters):
	new_centroid = []
	kk = 0
	for clust in list_of_clusters:
		size = len(clust)
		l, m, n, h = [], [], [], []
		for i in range(size):
			l.append(clust[i][0])
			m.append(clust[i][1])
			n.append(clust[i][2])
			h.append(clust[i][3])
		new_centroid.append(list({mean(l),mean(m),mean(n),mean(h)}))
	print(new_centroid)
	return new_centroid

